mapper.select:
select t.* from ( SELECT id , type , status , name , amount , address , date_format(t.s_date,'%Y-%m-%d') as s_date , date_format(t.e_date,'%Y-%m-%d') as e_date FROM sjlr_package t WHERE t.del_flag = 0
and ${filter}
ORDER BY t.ID DESC ) t limit ${start},${limit}

mapper.update:
update sys_role set role_name=#{role_name},
role_info=#{role_info},
role_main=#{role_main}, update_user=#{update_user}, update_date=#{update_date} where id=#{id}
