mapper.select:
select t.* from ( select code, DATE_FORMAT(touch_date, '%Y-%m-%d %H:%i:%s') as touch_date, case when current_bill_date is null then DATE_FORMAT(now(), '%Y-%m-%d') else DATE_FORMAT(current_bill_date, '%Y-%m-%d') end as current_bill_date, TIMESTAMPDIFF(DAY,now(),current_bill_date)+ case when bill_repay_day_count is null then 0 else bill_repay_day_count end as fee_free_day_count, total_credit - bill_amount - pre_bill_amount - remaining_credit as problem_money, name, inherent_credit, temporary_credit, total_credit, remaining_credit, remaining_credit_percentage, bill_amount, pre_bill_amount, month_bill_date, count from wj_bank
${filter}
ORDER BY ${sort} ${dir} ORDER BY ID ) t limit ${start},${limit}

mapper.update:
update wj_bank set  bill_amount=#{bill_amount},  
pre_bill_amount=#{pre_bill_amount},
remaining_credit = #{remaining_credit}
where code = #{repayment_bank}

update wj_financial_information set next_day_arrival = #{next_day_arrival}, bank_amount = #{bank_amount},
debt = #{debt}, financial_product_amount = #{financial_product_amount}, financial_product_amount_income = #{financial_product_amount_income}, cash = #{cash}, travel_expenses = #{travel_expenses}, instalment_amount = #{instalment_amount}, income_and_expenditure = #{income_and_expenditure}
where 1=1

mapper.select:
SELECT t.* FROM wj_bank t
where 1=1 

 	and code = #{repayment_bank}
